import { useEffect, useMemo, useState } from 'react';
import { createParlay, deleteParlay, getPlayers, getTeams, listParlays, updateParlay } from './api';

function emptyLeg() {
  return { leg_type: 'team', team_id: '', player_id: '', market: '', selection: '', odds: '', result: 'pending' };
}

export default function ParlayForm() {
  const [teams, setTeams] = useState([]);
  const [players, setPlayers] = useState([]);
  const [parlays, setParlays] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const [form, setForm] = useState({
    date: '',
    stake: '',
    potential_payout: '',
    sportsbook: '',
    status: 'pending',
    notes: '',
    legs: [emptyLeg()],
  });

  useEffect(() => {
    (async () => {
      const [t, p] = await Promise.all([getTeams(), getPlayers()]);
      setTeams(t);
      setPlayers(p);
      await refreshParlays();
    })();
  }, []);

  async function refreshParlays() {
    const items = await listParlays();
    setParlays(items);
  }

  function setLeg(idx, patch) {
    setForm(prev => {
      const legs = prev.legs.map((l, i) => (i === idx ? { ...l, ...patch } : l));
      return { ...prev, legs };
    });
  }

  function addLeg() {
    setForm(prev => ({ ...prev, legs: [...prev.legs, emptyLeg()] }));
  }

  function removeLeg(idx) {
    setForm(prev => ({ ...prev, legs: prev.legs.filter((_, i) => i !== idx) }));
  }

  function resetForm() {
    setEditingId(null);
    setForm({ date: '', stake: '', potential_payout: '', sportsbook: '', status: 'pending', notes: '', legs: [emptyLeg()] });
  }

  async function onSubmit(e) {
    e.preventDefault();
    const payload = {
      ...form,
      stake: parseFloat(form.stake || 0),
      potential_payout: form.potential_payout ? parseFloat(form.potential_payout) : null,
      date: form.date,
      legs: form.legs.map(l => ({
        ...l,
        team_id: l.leg_type === 'team' && l.team_id ? Number(l.team_id) : null,
        player_id: l.leg_type === 'player' && l.player_id ? Number(l.player_id) : null,
        odds: l.odds === '' ? null : Number(l.odds),
      })),
    };
    if (editingId) {
      await updateParlay(editingId, payload);
    } else {
      await createParlay(payload);
    }
    await refreshParlays();
    resetForm();
  }

  function startEdit(p) {
    setEditingId(p.id);
    setForm({
      date: p.date,
      stake: String(p.stake),
      potential_payout: p.potential_payout != null ? String(p.potential_payout) : '',
      sportsbook: p.sportsbook || '',
      status: p.status,
      notes: p.notes || '',
      legs: p.legs.map(l => ({
        leg_type: l.leg_type,
        team_id: l.team_id || '',
        player_id: l.player_id || '',
        market: l.market,
        selection: l.selection,
        odds: l.odds == null ? '' : String(l.odds),
        result: l.result,
      })),
    });
  }

  async function removeParlay(id) {
    await deleteParlay(id);
    if (editingId === id) resetForm();
    await refreshParlays();
  }

  return (
    <div style={{ display: 'grid', gap: 16 }}>
      <h2>{editingId ? 'Edit Parlay' : 'Create Parlay'}</h2>

      <form onSubmit={onSubmit} style={{ display: 'grid', gap: 8 }}>
        <label>
          Date
          <input type="date" value={form.date} onChange={e => setForm({ ...form, date: e.target.value })} required />
        </label>
        <label>
          Stake ($)
          <input type="number" step="0.01" value={form.stake} onChange={e => setForm({ ...form, stake: e.target.value })} required />
        </label>
        <label>
          Potential Payout ($)
          <input type="number" step="0.01" value={form.potential_payout} onChange={e => setForm({ ...form, potential_payout: e.target.value })} />
        </label>
        <label>
          Sportsbook
          <input value={form.sportsbook} onChange={e => setForm({ ...form, sportsbook: e.target.value })} />
        </label>
        <label>
          Status
          <select value={form.status} onChange={e => setForm({ ...form, status: e.target.value })}>
            <option value="pending">pending</option>
            <option value="won">won</option>
            <option value="lost">lost</option>
          </select>
        </label>
        <label>
          Notes
          <textarea value={form.notes} onChange={e => setForm({ ...form, notes: e.target.value })} />
        </label>

        <div style={{ borderTop: '1px solid #ccc', paddingTop: 8 }}>
          <h3>Legs</h3>
          {form.legs.map((leg, idx) => (
            <div key={idx} style={{ display: 'grid', gridTemplateColumns: 'repeat(6, 1fr) auto', gap: 8, alignItems: 'end' }}>
              <label>
                Type
                <select value={leg.leg_type} onChange={e => setLeg(idx, { leg_type: e.target.value })}>
                  <option value="team">team</option>
                  <option value="player">player</option>
                </select>
              </label>

              {leg.leg_type === 'team' ? (
                <label>
                  Team
                  <select value={leg.team_id} onChange={e => setLeg(idx, { team_id: e.target.value, player_id: '' })}>
                    <option value="">Select team</option>
                    {teams.map(t => (
                      <option key={t.id} value={t.id}>{t.name}</option>
                    ))}
                  </select>
                </label>
              ) : (
                <label>
                  Player
                  <select value={leg.player_id} onChange={e => setLeg(idx, { player_id: e.target.value, team_id: '' })}>
                    <option value="">Select player</option>
                    {players.map(p => (
                      <option key={p.id} value={p.id}>{p.name}</option>
                    ))}
                  </select>
                </label>
              )}

              <label>
                Market
                <input value={leg.market} onChange={e => setLeg(idx, { market: e.target.value })} placeholder="Points/Assists/ML" />
              </label>
              <label>
                Selection
                <input value={leg.selection} onChange={e => setLeg(idx, { selection: e.target.value })} placeholder="Over 24.5 / BOS ML" />
              </label>
              <label>
                Odds
                <input type="number" value={leg.odds} onChange={e => setLeg(idx, { odds: e.target.value })} placeholder="-110 / 250" />
              </label>
              <label>
                Result
                <select value={leg.result} onChange={e => setLeg(idx, { result: e.target.value })}>
                  <option value="pending">pending</option>
                  <option value="won">won</option>
                  <option value="lost">lost</option>
                  <option value="push">push</option>
                </select>
              </label>
              <button type="button" onClick={() => removeLeg(idx)}>Remove</button>
            </div>
          ))}
          <button type="button" onClick={addLeg}>Add Leg</button>
        </div>

        <div style={{ display: 'flex', gap: 8 }}>
          <button type="submit">{editingId ? 'Update' : 'Create'}</button>
          {editingId && <button type="button" onClick={resetForm}>Cancel</button>}
        </div>
      </form>

      <div style={{ borderTop: '1px solid #ccc', paddingTop: 8 }}>
        <h3>Existing Parlays</h3>
        {parlays.length === 0 && <div>No parlays yet.</div>}
        <ul style={{ listStyle: 'none', padding: 0, display: 'grid', gap: 8 }}>
          {parlays.map(p => (
            <li key={p.id} style={{ border: '1px solid #ddd', padding: 8, borderRadius: 4 }}>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <strong>{p.date} • ${p.stake} • {p.status}</strong>
                <div style={{ display: 'flex', gap: 8 }}>
                  <button onClick={() => startEdit(p)}>Edit</button>
                  <button onClick={() => removeParlay(p.id)}>Delete</button>
                </div>
              </div>
              <div>Legs: {p.legs.map(l => l.selection).join(', ')}</div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
